$(document).ready(function(){$(".symbolRowLeft").marquee({duration:4e4,gap:60,delayBeforeStart:0,direction:"right",duplicated:!0,startVisible:!0}),$(".symbolRowRight").marquee({duration:25e3,gap:60,delayBeforeStart:0,direction:"left",duplicated:!0,startVisible:!0}),$(".toggle-overlay").click(function(){$(".about").toggleClass("open")}),$(document).ready(function(){$("body").mousemove(function(e){$(".cursor").css("left",e.pageX),$(".cursor").css("top",e.pageY)})}),$("a, .toggle-overlay").hover(function(){$(".cursor").toggleClass("hovering")}),$(".lazy").Lazy({scrollDirection:"vertical",effect:"fadeIn",visibleOnly:!0,onError:function(e){console.log("error loading "+e.data("src"))}})});